        <script>

$(document).ready(() => {
          var cont = document.querySelector('#cont');
          var native = document.querySelector('#native');
          var back = document.querySelector('#back');
          var native_btn = document.querySelector('#native_btn');
          var back_btn = document.querySelector('#back_btn');

          native_btn.addEventListener('click', () => {
            cont.classList.toggle("rm_hidder");
            native.classList.toggle("rm_hidder");
            back.classList.toggle("rm_hidder");
          });
          back_btn.addEventListener('click', () => {
            cont.classList.toggle("rm_hidder");
            native.classList.toggle("rm_hidder");
            back.classList.toggle("rm_hidder");
          });
        });


          var login = document.querySelector('#log-in');
          var signup = document.querySelector('#sign-up');
          var signupDiv = document.querySelector('#signup');
          var loginDiv = document.querySelector('#cont1');
          var signupTitle = document.querySelector('#SignupTitle');
          var logintitle = document.querySelector('#loginTitle');
          var haveAc = document.querySelector('#haveAc');
          var haveAcNot = document.querySelector('#haveAcNot');


          var formSwtichBtn = document.querySelectorAll('#formSwtichBtn');

          formSwtichBtn.forEach(btn => {
            btn.addEventListener('click', () => {
              loginDiv.classList.toggle('rm_hidder');
              signupDiv.classList.toggle('rm_hidder');
              signupTitle.classList.toggle('rm_hidder');
              logintitle.classList.toggle('rm_hidder');
              haveAc.classList.toggle('rm_hidder');
              haveAcNot.classList.toggle('rm_hidder');
            });
          });

      
      $(document).ready(() => {
      
      var signUpForm = $('#sign-up');
      var logInForm = $('#log-in');
      
      $(signUpForm).on('submit', function(event){
          event.preventDefault();
      
          var url = $(this).attr('action');
      
          $.ajax({
              url: url,
              method: 'POST',
              data: new FormData(this),
              dataType: 'JSON',
              contentType: false,
              cache: false,
              processData: false,
              success:function(response)
              {
                  $(signUpForm).trigger("reset");
                  if(response.success == 'user registered succesfully'){
                  window.location.href = '/Log-in';
                }
              },
              error: function(response) {
                  console.log(response);
                  $('#notiBox').removeClass('rm_hidder');
                  $('#errorBox').html(response.success);
              }
          });
      });
      $(logInForm).on('submit', function(event){
          event.preventDefault();
      
          var url = $(this).attr('action');
      
          $.ajax({
              url: url,
              method: 'POST',
              data: new FormData(this),
              dataType: 'JSON',
              contentType: false,
              cache: false,
              processData: false,
              success:function(response)
              {
                $(signUpForm).trigger("reset");
                if(response.success == 'user login succesfully'){
                  window.location.href = '/';
                }
              },
              error: function(response) {
                $('#notiBox').removeClass('rm_hidder');
                  $('#errorBox').html(response.success);
              }
          });
      });
      
      });
      </script>
