</style>

<script>
    function rangetime() {
        return {
            from: 0,
            to: 120,
            min: 0,
            max: 120,
            mainColor: 'rgb(44, 140, 244)',
            bgColor: 'rgb(227, 233, 237)',

            fillColor() {
                let percent1 = (this.from / this.max) * 100;
                let percent2 = (this.to / this.max) * 100;
                this.$refs.sliderTrack.style.background = `linear-gradient(to right, ${this.bgColor} ${percent1}% , ${this.mainColor} ${percent1}% , ${this.mainColor} ${percent2}%, ${this.bgColor} ${percent2}%)`;
            },

            minTrigger() {
                this.from = Math.min(this.from, this.to);
            },

            maxTrigger() {
                this.to = Math.max(this.to, this.from);
            },

            fromFormatted() {
                                    return this.toHuman(this.from);
                            },

            toFormatted() {
                                    return this.toHuman(this.to);
                            },

            toMaxed() {
                return this.to === this.max;
            },

            toHuman(value) {
                const minutes = parseInt(value / 60);
                const seconds = value % 60;

                return `${`${minutes}`.padStart(2, '0')}:${`${seconds}`.padStart(2, '0')}`
            },

            sendRangesToDataLayer() {
                this.clearRanges()
                this.updateDataLayer(`${this.from}-${this.to}`)
            },

            clearRanges() {
                dataLayer = dataLayer.filter(event => event.filter_category !== 'range')
            },

            updateDataLayer(range) {
                dataLayer.push({
                    'filter_name': `duration : ${range}`,
                })
            }
        }
    }
</script>
