    <script>
        /** TODO move to another file?? */
        function getUrl(id, download_option, premium) {
            let requestBody = JSON.stringify({
                "id": id,
                "download_option": download_option
            });

            fetch('/download', {
                    method: 'POST',
                    body: requestBody
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success === true) {
                        // double redirection for premium videos
                        if (premium === 1) {
                            // internal signed url
                            fetch(data.url)
                                .then(response => response.json())
                                // external signed url
                                .then(premiumData => {
                                    if (premiumData.success === true) {
                                        clickDownload(premiumData.url, true);
                                    }
                                })
                        } else {
                            // just get the free resource
                            clickDownload(data.url, true);
                        }
                    } else {
                        clickDownload(data.url, false, data.inNewPage);
                    }
                });
        }

        function clickDownload(url, success, inNewPage = true) {
            const link = document.createElement('a');
            link.href = url;
            if (success) {
                link.setAttribute('download', '');
            } else {
                if (inNewPage === true) {
                    link.setAttribute('target', '_blank')
                }
            }
            link.style.display = 'none';
            document.body.appendChild(link);
            link.click();
            link.remove();
        }

        function downloadButton() {
            return {
                onDownload($dispatch, el) {
                    const hashParts = window.location.hash.substr(1).split('&');

                    hashParts.forEach((part) => {
                        if (part.indexOf('search-') >= 0) {
                            let search = part.substr(7);
                            search = decodeURIComponent(search).replace('+', ' ');

                            document.querySelector('#video_search').value = search;
                        }
                        if (part.indexOf('position-') >= 0) {
                            document.querySelector('#video_position').value = part.substr(9);
                        }
                        if (part.indexOf('results-') >= 0) {
                            document.querySelector('#video_total_results').value = part.substr(8);
                        }
                    })
                    $dispatch('download');
                }
            }
        }


        function series() {
            return {
                showMore() {
                    const boxesHidden = document.querySelectorAll('.more-relateds.hidden');
                    boxesHidden.forEach((el, index) => {
                        if (index >= 18) return;
                        el.classList.remove('hidden');
                    })
                    boxesHidden.length <= 18 && document.querySelector('#show-more').classList.add('hidden')
                }
            }
        }
        function sameClipsAreShowable() {
            return (document.querySelectorAll('#same-component .clip-wrapper')?.length ?? 0) > 0;
        }

    </script>
