window.addEventListener('load', function () {

    /* page_view */
    dataLayer.push({
        'event': 'page_view',
        'page_location': window.location.href,
        'page_title': document.title,
        'location_href': document.location.href,
        'page_type' : 'audio.sound-effects.all',
