    <script>

window.addEventListener('load', function () {

    /* page_view */
    dataLayer.push({
        'event': 'page_view',
        'page_location': window.location.href,
        'page_title': document.title,
        'location_href': document.location.href,
        'page_type' : 'audio.sound-effects.all',
        'page_code' : '200',
        'page_lang' : 'en',
        'user_id' : '',
        'user_login_status' : '',
        'user_registration_date' : '',
        'user_payment_start_date' : '',
        'user_payment_end_date' : '',
        'user_type' : '',
    })
    
    
    Livewire.on('filterChange', filterData => {
        console.log(filterData);
        layerData = ``;
        for (const [key, value] of Object.entries(filterData)) {
            filterValue = value;
            if (typeof value === 'object') {
                filterValue = value.join('|');
            }
            if (!value) {
                filterValue = '-';
            }
            layerData += `${key} : ${filterValue} ; `;
        }
        dataLayer.push({
            'apply_filter': layerData,
        });
    })

    document.addEventListener("click", function (e) {
        // general event tracking
        if ( e.target && e.target.matches("[gtm-event]") ) {
            let eventName = e.target.attributes["gtm-event"].value
            let label = e.target.attributes["gtm-label"].value || eventName;
            let category = e.target.attributes["gtm-category"].value || eventName;
            let layerData = {
                'event': eventName,
                'category': category,
                'action': 'Clicked',
                'label': label,
                'value': 1
            }
            dataLayer.push(layerData);
        }

    });
    document.addEventListener("click", function (e) {
        // general event tracking
        if ( e.target && e.target.matches("[shutterstock-clip]") ) {
            let label = e.target.attributes["shutterstock-clip"].value;
            let layerData = {
                'event': "shuttertockEvent",
                'category': "Shutterstock",
                'action': 'Clicked',
                'label': `Shutterstock : ${label}`,
                'value': 1
            }
            dataLayer.push(layerData);
        }

    });

    document.querySelectorAll(`[data-related-videos="true"] [video-anchor]`).forEach( (el) => {
        el.addEventListener('click', function (e) {
            e.preventDefault();
            let layerData = {
                'event': 'click_on_a_similar_clip_in_download_page',
                'category': "Similar Clip download page",
                'action': 'Click Similar Clip download page',
                'label': "Click Similar Clip download page",
                'value': 1
            }

            console.log(layerData);
            dataLayer.push(layerData);
        }); 
    }); 


    Livewire.on('toggleFavorite', () => {
        let layerData = {
            'event': 'click_on_secondary_actions',
            'category': "Secondary Action",
            'action': 'Secondary Action',
            'label': "Secondary Action",
            'value': 1
        }
        dataLayer.push(layerData);
    })

    Livewire.on('toggleAddToCollection', () => {
        let layerData = {
            'event': 'click_on_secondary_actions',
            'category': "Secondary Action",
            'action': 'Secondary Action',
            'label': "Secondary Action",
            'value': 1
        }
        dataLayer.push(layerData);
    })

    Livewire.on('addToCollectionNotLoggedIn', () => {
        let layerData = {
            'event': 'add_to_collection_not_logged_in',
            'category': "Add to Collection Not Logged In",
            'action': 'Add to Collection Not Logged In',
            'label': "Add to Collection Not Logged In",
            'value': 1
        }
        dataLayer.push(layerData);
    })

    Livewire.on('addToCollectionLoggedIn', () => {
        let layerData = {
            'event': 'add_to_collection_logged_in',
            'category': "Add to Collection Logged In",
            'action': 'Add to Collection Logged In',
            'label': "Add to Collection Logged In",
            'value': 1
        }
        dataLayer.push(layerData);
    })

});


