    <script>
        function getUrl(id, download_option, premium) {
            let requestBody = JSON.stringify({
                "id": id,
                "download_option": download_option
            });

            fetch('/download', {
                method: 'POST',
                body: requestBody
            })
                .then(response => response.json())
                .then(data => {
                    if (data.success === true) {
                        // double redirection for premium videos
                        if (premium === 1) {
                            // internal signed url
                            fetch(data.url)
                                .then(response => response.json())
                                // external signed url
                                .then(premiumData => {
                                    if (premiumData.success === true) {
                                        clickDownload(premiumData.url, true);
                                    }
                                })
                        } else {
                            // just get the free resource
                            clickDownload(data.url, true);
                        }
                    } else {
                        clickDownload(data.url, false);
                    }
                });
        }

        function clickDownload(url, success) {
            const link = document.createElement('a');
            link.href = url;
            if (success) {
                link.setAttribute('download', '');
            } else {
                link.setAttribute('target', '_blank');
            }
            link.style.display = 'none';
            document.body.appendChild(link);
            link.click();
            link.remove();
        }
    </script>
</div>

